mapper.select:
SELECT Id as id, UserId as userId, VideoId as videoId, Emails as emails, ShareDate as shareDate
FROM Share
WHERE UserId = #{userId} AND VideoId = #{videoId} AND Emails = #{emails}

mapper.update:
UPDATE Share
SET UserId = #{userId},
VideoId = #{videoId},
Emails = #{emails},
ShareDate = #{shareDate}
WHERE Id = #{id}
